import React from 'react';
import { useLocalStorage } from '@uidotdev/usehooks';

const Search: React.FC = () => {
  const [value, setValue] = useLocalStorage('searchTerm', '');

  return (
    <div style={{margin: '20px'}}>
      <input
        type="text"
        placeholder="Search by ID or full name"
        value={value}
        onChange={(event) => setValue(event.target.value)}
      />
    </div>
  );
};

export default Search;

import React from 'react';

type PaginationProps = {
  page: number;
  setPage: (page: number) => void;
};

const Pagination: React.FC<PaginationProps> = ({ page, setPage }) => {
  return (
    <>
      <button disabled={page === 1} onClick={() => setPage(page - 1)}>
        prev page
      </button>
      <button disabled={page === 1} onClick={() => setPage(1)}>
        1
      </button>
      <button disabled={page === 2} onClick={() => setPage(2)}>
        2
      </button>
      <button disabled={page === 3} onClick={() => setPage(3)}>
        3
      </button>
      <button disabled={page === 4} onClick={() => setPage(4)}>
        4
      </button>
      <button disabled={page === 5} onClick={() => setPage(5)}>
        5
      </button>
      <button disabled={page === 6} onClick={() => setPage(6)}>
        6
      </button>
      <button disabled={page === 7} onClick={() => setPage(7)}>
        7
      </button>
      <button disabled={page === 7} onClick={() => setPage(page + 1)}>
        next page
      </button>
    </>
  );
};

export default Pagination;

import { createApi, fetchBaseQuery } from '@reduxjs/toolkit/query/react';

export const apiSlice = createApi({
  reducerPath: 'api',
  baseQuery: fetchBaseQuery({ baseUrl: 'https://pokeapi.co/api/v2' }),
  endpoints: (builder) => ({
    getBerries: builder.query({
      query: ({ offset, limit, searchTerm }: { offset: number, limit: number, searchTerm: string }) => {
        if (searchTerm) {
          return `berry/${searchTerm}`;
        }
        return `berry/?offset=${offset}&limit=${limit}`;
      },
    }),
    getBerryById: builder.query({
      query: (name: string) => {
        return `berry/${name}`;
      }
    })
  }),
});

export const { useGetBerriesQuery, useGetBerryByIdQuery } = apiSlice;

import { TypedUseSelectorHook, useDispatch, useSelector } from 'react-redux';
import type { RootState, AppDispatch } from '../store';

export const useAppDispatch = () => useDispatch<AppDispatch>();
export const useAppSelector: TypedUseSelectorHook<RootState> = useSelector;

import { createSlice, PayloadAction } from '@reduxjs/toolkit';
import { apiSlice } from '../query/apiSlice';

interface ItemType {
  id: string;
  name: string;
}

interface SelectedItemsSlice {
  items: ItemType[];
}

const initialState: SelectedItemsSlice = {
  items: [],
};

export const selectedItemsSlice = createSlice({
  name: 'selectedItems',
  initialState,
  reducers: {
    selectItem: (state, action: PayloadAction<ItemType>) => {
      const exist = state.items.find((item) => item.id === action.payload.id);
      if (exist) {
        state.items = state.items.filter(
          (item) => item.id !== action.payload.id,
        );
      } else {
        state.items.push(action.payload);
      }
    },
    unselectAll: (state) => {
      state.items = []
    }
  }
});

export const {selectItem, unselectAll} = selectedItemsSlice.actions;
export default selectedItemsSlice.reducer;

import React, { useEffect, useState } from 'react';
import { useLocalStorage } from '@uidotdev/usehooks';
import { NavLink, useNavigate, useSearchParams } from 'react-router-dom';
import Pagination from '../pagination/pagination';
import { useGetBerriesQuery } from '../../redux/query/apiSlice';
import { useAppDispatch, useAppSelector } from '../../redux/hooks/hooks';
import { selectItem, unselectAll } from '../../redux/slices/selectedItemsSlice';

interface Berry {
  name: string;
  url: string;
}

const Result: React.FC = () => {
  const [searchTerm] = useLocalStorage<string>('searchTerm', '');
  const navigate = useNavigate();
  const [searchParams] = useSearchParams();
  const initialPage = parseInt(searchParams.get('page') || '1', 10);
  const [page, setPage] = useState(initialPage);
  const dispatch = useAppDispatch();
  const selectedItems = useAppSelector((state) => state.selectedItems.items);

  const offset = (page - 1) * 10;
  const { data, error, isLoading } = useGetBerriesQuery({
    offset,
    limit: 10,
    searchTerm,
  });

  useEffect(() => {
    navigate(`?page=${page}`);
  }, [page, navigate]);

  const handleCheckboxChange = (berry: Berry) => {
    dispatch(selectItem({ id: berry.url, name: berry.name }));
  };

  const isSelected = (id: string) =>
    selectedItems.some((item) => item.id === id);

  const handleDownload = () => {
    const csvContent =
      'data:text/csv;charset=utf-8,' +
      + selectedItems.map((item) => `${item.id},${item.name}`).join('\n');
    const encodedUri = encodeURI(csvContent);
    const link = document.createElement('a');
    link.setAttribute('href', encodedUri);
    link.setAttribute('download', `${selectedItems.length}_berries.csv`);
    link.click();
  };

  if (error) return <div>Ooops</div>;

  return (
    <div>
      {!isLoading ? (
        <div>
          {data?.results?.length ? (
            data.results.map((el: Berry, idx: number) => (
              <div key={idx}>
                <input
                  type="checkbox"
                  checked={isSelected(el.url)}
                  onChange={() => handleCheckboxChange(el)}
                />
                <NavLink to={`/SearchedItem/${el.name}`}>{el.name}</NavLink>
              </div>
            ))
          ) : (
            <NavLink to={`/SearchedItem/${data.name}`}>{data.name}</NavLink>
          )}
        </div>
      ) : (
        <div style={{ marginTop: '20px' }}>Loading...</div>
      )}
      <div style={{ marginTop: '20px' }}>
        <Pagination page={page} setPage={setPage} />
      </div>
      {selectedItems.length > 0 && (
        <div className="flyout">
          <p>{selectedItems.length} items are selected</p>
          <button onClick={() => dispatch(unselectAll())}>Unselect all</button>
          <button onClick={handleDownload}>Download</button>
        </div>
      )}
    </div>
  );
};

export default Result;

import React from 'react';
import Search from '../components/search/search';
import Result from '../components/result/result';
import { Outlet } from 'react-router-dom';

const SearchPage: React.FC = () => {
  return (
    <>
      <div style={{ display: 'flex', gap: '50px', justifyContent: 'center' }}>
        <div style={{ width: '260px' }}>
          <Search />
          <Result />
        </div>
        <Outlet />
      </div>
    </>
  );
};

export default SearchPage;

import React from 'react';

const NotFoundPage: React.FC = () => {
  return (
    <div>
      <h1 style={{ textAlign: 'center', color: '#ffffff' }}>not found</h1>
    </div>
  );
};

export default NotFoundPage;

import React from 'react';
import { Link, useParams } from 'react-router-dom';
import { useGetBerryByIdQuery } from '../redux/query/apiSlice';

const SearchedItem: React.FC = () => {
  const { name } = useParams<{ name: string }>();
  const { data: pokemon, error, isLoading } = useGetBerryByIdQuery(name);

  if (isLoading) return <div style={{ marginTop: '20px' }}>Loading ...</div>;
  if (error) return <div>Ooops</div>;

  return (
    <div>
      <div>
        <Link to="/">
          <button hidden={!pokemon}>
            close
          </button>
        </Link>
      </div>
      <div>
        <h3>{pokemon?.name}</h3>
      </div>
    </div>
  );
};

export default SearchedItem;

import React, { createContext, ReactNode, useContext, useState } from 'react';
import './index.css'

type Theme = 'dark' | 'light';

interface ThemeContextProps {
  theme: Theme;
  toggleTheme: () => void;
}

const ThemeContext = createContext<ThemeContextProps | undefined>(undefined);

export const useTheme = () => {
  return useContext(ThemeContext);
};

export const ThemeProvider: React.FC<{ children: ReactNode }> = ({ children }) => {
  const [theme, setTheme] = useState<Theme>('dark');

  const toggleTheme = () => {
    setTheme((prevTheme) => (prevTheme === 'light' ? 'dark' : 'light'));
  };

  return (
    <ThemeContext.Provider value={{ theme, toggleTheme }}>
      <div className={theme}>
        {children}
      </div>
    </ThemeContext.Provider>
  );
};

import React, { useEffect } from 'react';
import { Route, Routes } from 'react-router-dom';
import './App.css';
import SearchPage from './pages/SearchPage';
import NotFoundPage from './pages/404-page';
import SearchedItem from './pages/SearchedItem';
import { ThemeProvider, useTheme } from './themeContext';

const ThemeToggleButton: React.FC = () => {
  const { theme, toggleTheme } = useTheme();

  return (
    <button onClick={toggleTheme}>
      Switch to {theme === 'light' ? 'dark' : 'light'} theme
    </button>
  );
};

const App: React.FC = () => {
  return (
    <ThemeProvider>
      <div className="App">
        <ThemeToggleButton />
        <Routes>
          <Route path="/" element={<SearchPage />}>
            <Route path="SearchedItem/:name" element={<SearchedItem />} />
          </Route>
          <Route path="*" element={<NotFoundPage />} />
        </Routes>
      </div>
    </ThemeProvider>
  );
};

export default App;
